function changeLang(lang_l){
	//console.log(lang_l);
	if(lang_l=='en') return false;
	lang = lang_l=='ua'?0:1;
	$('text,title,li a,#appliedFilters li').each(function(i){
		var text = $(this).text().split(": ");
		if(translate_text[text[0]]) $(this).text($(this).text().replace(text[0],translate_text[text[0]][lang]));
		if(!!text[1] && !Number.isInteger(-text[1]) && text[1]!='undefined'){
			var text2 = text[1].split(", ");

			for (var i = 0; i < text2.length; i++) {
				if(translate_text[text2[i]]) $(this).text($(this).text().replace(text2[i],translate_text[text2[i]][lang]));
			}
			$(this).text($(this).text().replace(' ,',''));
			
		}

	});
	setTimeout(function(){ changeLang(lang_l)}, 100);
}

